function formatint($number) {
    $prefixes = 'kMGTPEZY';
    if ($number >= 10000) {
        for ($i=-1; $number>=1000; ++$i) {
            $number /= 1000;
        }
        return floor($number).$prefixes[$i];
    }
    return $number;
}



