	{
		$isshow=0;
	}
	if ($_POST['showmode']=="showall")
	{
		//$isshow=1;
	}
	if (($_POST['showmode']=="showfav") && ((!in_array($link,$orderfavs)) || (in_array($link,$orderspams))))
	{
		$isshow=0;
	}
	if ($_POST['unrep']=='on')
	{
		if (in_array($content,$mas_rep))
